import React, {Component} from 'react';
import Cardlist from './Cardlist';

class App extends Component {
  constructor () {
    super()
    this.state = {
      movies: []
    }
  }
  componentDidMount() {
    const apiUrl = 'https://api.themoviedb.org/3/discover/movie?api_key=544297d08ee9679e6641d18e5056a278&language=en-US&sort_by=popularity.desc&include_adult=false&include_video=false&page=1' 
    fetch(apiUrl)
    .then(response => response.json())
    .then(data => console.log(data))
    .catch(error => console.log(error));
  }
  storeMovies = data => {
    const movies = data.results.map(result => {
      const  { vote_count, id, genre_ids, poster_path, title, vote_average, release_date } = result;
      return { vote_count, id, genre_ids, poster_path, title, vote_average, release_date };
    });
    this.setState({ movies })
  }
  render() {
    return (
      <div>
        <h1 className="f1 white tc">ScreenTime 🍿</h1>
        {this.state.movies.map ( movie => (
          <Cardlist key={movie.id} movie={movie} />
        ))}
      </div>
    )
  }
}

export default App;
